function resizeSeo(){
    if(jQuery('.seo-abs').length){
        height = jQuery('.seo-abs').height()+50;
        width = jQuery('.seo-abs').width();
        pos = jQuery('.seo-fix').position();
        jQuery('.seo-fix').height(height);

        jQuery('.seo-abs').css({top:pos.top }); 
    }

}
	
jQuery(document).ready(function($){
    resizeSeo();
});	
jQuery(window).load(function($){
    resizeSeo();
});	
jQuery(window).resize(function($){
    resizeSeo();
});